mapper.select:
select * from user where true

  and id=#{id}


    and name=#{name}


    and auth=#{auth}


    and sex=#{sex}


    and isArtist=1


    order by id desc

limit  #{limit} offset #{offset}

select a.*,u.name,u.sex from artist a left join user u on a.user_id=u.id  where true
and review=#{review}
and review=0 or review=1 or review=2 or review=3
limit #{limit} offset #{offset}
